import { useLocation, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import logo from '../icons/logo.svg';

export default function Header() {
  const location = useLocation();
  const navigate = useNavigate();

  const isAuthPage = location.pathname === '/login' || location.pathname === '/signup';

  const [dark, setDark] = useState(() => {
    const stored = localStorage.getItem('theme');
    return stored ? stored === 'dark' : true;
  });

  useEffect(() => {
    const forceDark = isAuthPage || dark;
    if (forceDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    if (!isAuthPage) {
      localStorage.setItem('theme', dark ? 'dark' : 'light');
    }
  }, [dark, isAuthPage]);

  const handleLogoClick = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
  };

  return (
    <div className="min-h-screen w-full bg-white dark:bg-zinc-900 text-black dark:text-white flex flex-col justify-center items-center transition-colors duration-300">
      <button
        onClick={handleLogoClick}
        className="flex items-center gap-3 hover:opacity-80 transition-opacity"
      >
        <img src={logo} alt="AS13.ai Logo" className="h-16 w-auto" />
        <span className="text-3xl font-extrabold text-blue-500">AS13.ai</span>
      </button>
    </div>
  );
}
