import { useLocation, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import logo from '../icons/logo.svg';
import Sidebar from './Sidebar'; // Optional: if you're rendering sidebar toggle here

export default function Header() {
  const location = useLocation();
  const navigate = useNavigate();
  const isAuthPage = location.pathname === '/login' || location.pathname === '/signup';

  const [dark, setDark] = useState(() => {
    const stored = localStorage.getItem('theme');
    return stored ? stored === 'dark' : true;
  });

  useEffect(() => {
    if (isAuthPage) {
      document.documentElement.classList.add('dark');
    } else {
      if (dark) {
        document.documentElement.classList.add('dark');
      } else {
        document.documentElement.classList.remove('dark');
      }
      localStorage.setItem('theme', dark ? 'dark' : 'light');
    }
  }, [dark, isAuthPage]);

  const handleLogoClick = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
  };

  return (
    <header className="bg-white dark:bg-zinc-900 text-black dark:text-white p-4 shadow-md fixed top-0 left-0 right-0 z-40 transition-colors duration-300">
      <div className="flex items-center ml-4">
        {/* LOGO FIRST */}
        <button
          onClick={handleLogoClick}
          className="flex items-center gap-2 hover:opacity-80 transition-opacity"
        >
          <img src={logo} alt="AS13.ai Logo" className="h-8 w-auto" />
          <span className="text-xl font-bold text-blue-500">AS13.ai</span>
        </button>

        {/* SIDEBAR TOGGLE OR COMPONENT (with left margin for spacing) */}
        <div className="ml-6">
          {/* If you're adding a toggle or button */}
          {/* <SidebarToggle dark={dark} setDark={setDark} /> */}

          {/* Or if you're embedding a component like theme toggle or mini sidebar */}
          {/* Example: dark mode toggle or user button */}
          {/* Add whatever you'd like to show here */}
          {/* Example placeholder below */}
          <button
            onClick={() => setDark(!dark)}
            className="bg-zinc-800 text-white px-3 py-1 rounded-lg text-sm hover:bg-zinc-700"
          >
            {dark ? 'üåô Dark' : '‚òÄÔ∏è Light'}
          </button>
        </div>
      </div>
    </header>
  );
}
