import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import {
  Menu,
  X,
  Plus,
  LogOut,
  Settings,
  Sparkles
} from "lucide-react";
import { AuthContext } from "../context/AuthContext"; // Make sure this path is correct

export default function Sidebar() {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();
  const { logout } = useContext(AuthContext); // ðŸ‘ˆ use logout from context

  const toggleSidebar = () => setIsOpen(!isOpen);

  const handleNewChat = () => {
    localStorage.removeItem("messages");
    navigate("/chat");
    setIsOpen(false);
  };

  const handleLogout = () => {
    logout(); // clear context + localStorage
    alert("You have logged out.");
    navigate("/login");
    setIsOpen(false);
  };

  return (
    <>
      {/* Sidebar Toggle Icon */}
      <div className="fixed top-4 left-4 z-50">
        <button
          onClick={toggleSidebar}
          className="text-white bg-zinc-900 p-2 rounded-full shadow hover:bg-zinc-800 transition"
        >
          <Menu className="w-5 h-5" />
        </button>
      </div>

      {/* Backdrop */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={toggleSidebar}
        />
      )}

      {/* Sidebar Panel */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-zinc-900 text-white flex flex-col z-50 shadow-lg transform transition-transform duration-300 ease-in-out ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        {/* Header */}
        <div className="p-4 border-b border-zinc-800 flex items-center justify-between">
          <span className="text-blue-500 text-xl font-bold">AS13.ai</span>
          <button
            onClick={toggleSidebar}
            className="text-gray-400 hover:text-white transition"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* New Chat */}
        <div className="p-4">
          <button
            onClick={handleNewChat}
            className="w-full flex items-center gap-2 px-4 py-2 text-sm font-medium bg-zinc-800 hover:bg-zinc-700 rounded-lg transition"
          >
            <Plus className="w-4 h-4" />
            New Chat
          </button>
        </div>

        {/* Recent Chats */}
        <div className="flex-1 overflow-y-auto px-4 pb-4 space-y-2 scrollbar-thin scrollbar-thumb-zinc-700 scrollbar-track-transparent">
          <p className="text-gray-400 text-xs uppercase mb-2 tracking-wide">
            Recent Chats
          </p>
          {[...Array(1)].map((_, idx) => (
            <div
              key={idx}
              className="bg-zinc-800 px-4 py-2 rounded-lg text-sm hover:bg-zinc-700 cursor-pointer transition"
            >
              Chat session {idx + 1}
            </div>
          ))}
        </div>

        {/* Footer */}
        <div className="p-4 border-t border-zinc-800 space-y-2">
          <button className="w-full flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-zinc-800 transition text-sm">
            <Sparkles className="w-4 h-4 text-yellow-400" />
            Upgrade Plan
          </button>
          <button className="w-full flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-zinc-800 transition text-sm">
            <Settings className="w-4 h-4" />
            Settings
          </button>
          <button
            onClick={handleLogout}
            className="w-full flex items-center gap-2 px-4 py-2 rounded-lg text-red-400 hover:bg-red-950 transition text-sm"
          >
            <LogOut className="w-4 h-4" />
            Logout
          </button>
        </div>
      </div>
    </>
  );
}
