import { useState, useEffect, useRef } from 'react';
import { marked } from 'marked';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Sidebar from './Sidebar';

export default function Chat({ dark, setDark }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [initializing, setInitializing] = useState(true);
  const bottomRef = useRef(null);
  const inputRef = useRef(null);
  const navigate = useNavigate();

  // Apply theme from localStorage on load
  useEffect(() => {
    const isDark = localStorage.getItem('theme') === 'dark';
    setDark(isDark);
    document.documentElement.classList.toggle('dark', isDark);
  }, [setDark]);

  // Load previous messages
  useEffect(() => {
    const isReload = !sessionStorage.getItem('visited');
    sessionStorage.setItem('visited', 'true');

    if (isReload) {
      localStorage.removeItem('messages');
      setMessages([]);
    } else {
      const saved = localStorage.getItem('messages');
      if (saved) setMessages(JSON.parse(saved));
    }

    setInitializing(false);
  }, []);

  // Scroll + Save messages
  useEffect(() => {
    if (!initializing) {
      localStorage.setItem('messages', JSON.stringify(messages));
    }
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  const sendMessage = async (msgText) => {
    const text = typeof msgText === 'string' ? msgText : input;
    if (!text.trim()) return;

    setMessages((prev) => [...prev, { role: 'user', text }]);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: text }),
      });
      const data = await res.json();
      setMessages((prev) => [...prev, { role: 'ai', text: data.reply }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { role: 'ai', text: '⚠️ Something went wrong. Please try again.' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const suggestedPrompts = [
    'Build a todo app in React using Tailwind',
    'Create a dark/light toggle in React',
    'Build a simple blog using Astro',
    'Write a REST API using Node.js',
  ];

  if (initializing) return null;

  return (
    <div className="flex flex-col min-h-screen bg-white dark:bg-black text-black dark:text-white transition duration-300">
      {/* Header */}
      <Header />

      {/* Layout below header */}
      <div className="flex-1 flex pt-16">
        {/* Sidebar */}
        <div className="hidden md:block fixed top-16 left-0 h-[calc(100vh-4rem)] w-64 z-20">
          <Sidebar dark={dark} setDark={setDark} />
        </div>

        {/* Chat Panel */}
        <div className="flex-1 md:ml-64 w-full px-3 sm:px-6 py-4 overflow-y-auto">
          <div className="max-w-3xl mx-auto">
            {/* Empty state */}
            {messages.length === 0 ? (
              <div className="text-center py-16 sm:py-24 px-2">
                <h1 className="text-3xl sm:text-4xl font-bold mb-2">
                  Where <span className="text-blue-500">ideas</span> begin
                </h1>
                <p className="text-sm sm:text-base text-gray-600 dark:text-gray-400 mb-6">
                  Ask questions or get help with your projects.
                </p>

                <div className="relative">
                  <textarea
                    ref={inputRef}
                    rows={3}
                    placeholder="How can AS13 help you today?"
                    className="w-full p-3 sm:p-4 pr-14 rounded-xl bg-zinc-100 dark:bg-zinc-900 border border-blue-600 text-black dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-600 resize-none"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                  />
                  <button
                    onClick={sendMessage}
                    disabled={!input.trim()}
                    className="absolute top-2 right-2 w-10 h-10 bg-blue-600 hover:bg-blue-700 disabled:bg-zinc-700 text-white rounded-lg flex items-center justify-center"
                    title="Send"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      className="h-5 w-5"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                      strokeWidth={2}
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" d="M4 4l16 8-16 8V4z" />
                    </svg>
                  </button>
                </div>

                <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                  {suggestedPrompts.map((prompt, index) => (
                    <button
                      key={index}
                      onClick={() => sendMessage(prompt)}
                      className="text-left px-4 py-3 rounded-xl bg-zinc-100 dark:bg-zinc-900 border border-zinc-300 dark:border-zinc-700 hover:bg-zinc-200 dark:hover:bg-zinc-800 text-sm sm:text-base font-medium"
                    >
                      {prompt}
                    </button>
                  ))}
                </div>
              </div>
            ) : (
              <>
                {/* Message history */}
                <div className="space-y-4 mb-6">
                  {messages.map((msg, idx) => (
                    <div
                      key={idx}
                      className={`p-4 rounded-xl text-sm sm:text-base whitespace-pre-wrap ${
                        msg.role === 'user'
                          ? 'bg-blue-100 text-black'
                          : 'bg-zinc-800 text-white'
                      }`}
                      dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
                    />
                  ))}
                  {loading && (
                    <div className="p-4 rounded-xl bg-zinc-800 text-white text-sm italic animate-pulse">
                      AS13 is typing...
                    </div>
                  )}
                  <div ref={bottomRef} />
                </div>

                {/* Input box */}
                <div className="py-4">
                  <div className="relative">
                    <textarea
                      ref={inputRef}
                      rows={2}
                      placeholder="Ask anything..."
                      className="w-full p-3 pr-14 rounded-xl bg-zinc-100 dark:bg-zinc-800 border border-zinc-300 dark:border-zinc-700 text-black dark:text-white placeholder-gray-500 dark:placeholder-gray-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-600"
                      value={input}
                      onChange={(e) => setInput(e.target.value)}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' && !e.shiftKey) {
                          e.preventDefault();
                          sendMessage();
                        }
                      }}
                    />
                    <button
                      onClick={sendMessage}
                      disabled={!input.trim()}
                      className="absolute top-2.5 right-2 w-10 h-10 bg-blue-600 hover:bg-blue-700 disabled:bg-zinc-700 text-white rounded-lg flex items-center justify-center"
                      title="Send"
                    >
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="h-5 w-5"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        strokeWidth={2}
                      >
                        <path strokeLinecap="round" strokeLinejoin="round" d="M4 4l16 8-16 8V4z" />
                      </svg>
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
