import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Menu, X, Plus, LogOut, Settings, Sparkles, Sun, Moon, ChevronDown, ChevronRight
} from 'lucide-react';
import { AuthContext } from '../context/AuthContext';

export default function Sidebar({ dark, setDark }) {
  const [isOpen, setIsOpen] = useState(false);
  const [showRecent, setShowRecent] = useState(true);
  const navigate = useNavigate();
  const { logout } = useContext(AuthContext);

  const toggleSidebar = () => setIsOpen(!isOpen);
  const toggleRecent = () => setShowRecent((prev) => !prev);

  const handleNewChat = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
    setIsOpen(false);
  };

  const handleLogout = () => {
    logout();
    alert('You have logged out.');
    navigate('/login');
    setIsOpen(false);
  };

  const goToSettings = () => {
    navigate('/settings');
    setIsOpen(false);
  };

  return (
    <>
      {/* Mobile Toggle */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <button
          onClick={toggleSidebar}
          className="bg-white dark:bg-zinc-900 border border-zinc-300 dark:border-zinc-700 p-2 rounded-lg shadow text-black dark:text-white"
        >
          {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
        </button>
      </div>

      {/* Sidebar */}
      <div
        className={`fixed md:relative top-16 md:top-0 left-0 z-40 w-64 h-[calc(100vh-64px)] md:h-auto bg-white dark:bg-zinc-900 border-r border-zinc-200 dark:border-zinc-800 transform transition-transform duration-300 ease-in-out p-4 ${
          isOpen ? 'translate-x-0' : '-translate-x-full md:translate-x-0'
        }`}
      >
        <div className="flex flex-col h-full justify-between">
          {/* Top Section */}
          <div>
            <button
              onClick={handleNewChat}
              className="flex items-center gap-2 px-3 py-2 w-full bg-blue-600 hover:bg-blue-700 text-white text-sm font-medium rounded-lg shadow transition"
            >
              <Plus className="w-4 h-4" />
              New Chat
            </button>

            {/* Recent Section Toggle */}
            <div className="flex items-center justify-between mt-6 mb-2 cursor-pointer" onClick={toggleRecent}>
              <p className="text-sm font-semibold text-zinc-500 dark:text-zinc-400">
                Recent
              </p>
              {showRecent ? (
                <ChevronDown className="w-4 h-4 text-zinc-500 dark:text-zinc-400" />
              ) : (
                <ChevronRight className="w-4 h-4 text-zinc-500 dark:text-zinc-400" />
              )}
            </div>

            {showRecent && (
              <div className="space-y-2">
                {[...Array(1)].map((_, idx) => (
                  <div
                    key={idx}
                    className="px-3 py-2 rounded-lg bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 text-sm cursor-pointer transition"
                  >
                    Chat session {idx + 1}
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="space-y-2 border-t border-zinc-200 dark:border-zinc-800 pt-3 mt-6">
            <button className="w-full flex items-center gap-2 px-3 py-2 rounded-lg hover:bg-zinc-100 dark:hover:bg-zinc-800 transition text-sm">
              <Sparkles className="w-4 h-4 text-yellow-400" />
              Upgrade to Pro
            </button>
            <button
              onClick={goToSettings}
              className="w-full flex items-center gap-2 px-3 py-2 rounded-lg hover:bg-zinc-100 dark:hover:bg-zinc-800 transition text-sm"
            >
              <Settings className="w-4 h-4" />
              Settings
            </button>
            <button
              onClick={handleLogout}
              className="w-full flex items-center gap-2 px-3 py-2 text-red-500 hover:bg-red-100 dark:hover:bg-red-950 rounded-lg transition text-sm"
            >
              <LogOut className="w-4 h-4" />
              Logout
            </button>
            <button
              onClick={() => setDark(!dark)}
              className="w-full flex items-center gap-2 px-3 py-2 bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 rounded-lg transition text-sm"
            >
              {dark ? (
                <>
                  <Sun className="w-4 h-4 text-yellow-400" />
                  Light Mode
                </>
              ) : (
                <>
                  <Moon className="w-4 h-4" />
                  Dark Mode
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
