import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from 'react-router-dom';
import { useEffect, useState } from 'react';
import { AuthProvider } from './context/AuthContext';

import Chat from './components/Chat';
import Login from './pages/Login';
import Signup from './pages/Signup';
import ProtectedRoute from './components/ProtectedRoute';
import Settings from './pages/Settings';
import Sidebar from './components/Sidebar';
import Header from './components/Header';

function App() {
  const [dark, setDark] = useState(() => {
    const stored = localStorage.getItem('theme');
    return stored ? stored === 'dark' : true;
  });

  useEffect(() => {
    if (dark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    localStorage.setItem('theme', dark ? 'dark' : 'light');
  }, [dark]);

  return (
    <AuthProvider>
      <Router>
        <AppContent dark={dark} setDark={setDark} />
      </Router>
    </AuthProvider>
  );
}

function AppContent({ dark, setDark }) {
  const location = useLocation();
  const isChatPage = location.pathname === '/chat';

  return (
    <div className="w-full">
      {/* Always show Header */}
      <Header dark={dark} setDark={setDark} />

      {isChatPage ? (
        <div className="flex pt-16 min-h-screen bg-black text-white">
          <Sidebar dark={dark} setDark={setDark} />
          <div className="flex-1">
            <Routes>
              <Route
                path="/chat"
                element={
                  <ProtectedRoute>
                    <Chat dark={dark} setDark={setDark} />
                  </ProtectedRoute>
                }
              />
            </Routes>
          </div>
        </div>
      ) : (
        <div className="pt-16">
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/settings" element={<Settings dark={dark} setDark={setDark} />} />
          </Routes>
        </div>
      )}
    </div>
  );
}

export default App;
