import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Menu,
  X,
  Plus,
  LogOut,
  Settings,
  Sparkles,
  Sun,
  Moon
} from 'lucide-react';
import { AuthContext } from '../context/AuthContext';

export default function Sidebar({ dark, setDark }) {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();
  const { logout } = useContext(AuthContext);

  const toggleSidebar = () => setIsOpen(!isOpen);

  const handleNewChat = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
    setIsOpen(false);
  };

  const handleLogout = () => {
    logout();
    alert('You have logged out.');
    navigate('/login');
    setIsOpen(false);
  };

  const goToSettings = () => {
    navigate('/settings');
    setIsOpen(false);
  };

  return (
    <div className="relative z-40">
      {/* Mobile Toggle Button */}
      <div className="md:hidden px-4 pt-2">
        <button
          onClick={toggleSidebar}
          className="text-zinc-900 dark:text-white p-2 rounded hover:bg-zinc-100 dark:hover:bg-zinc-800 transition"
          aria-label="Toggle Sidebar"
        >
          {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
        </button>
      </div>

      {/* Sidebar Container */}
      <div className={`md:block ${isOpen ? 'block' : 'hidden'} w-full`}>
        <aside className="w-full md:w-64 bg-white dark:bg-zinc-900 text-black dark:text-white border-t md:border-t-0 md:border-r border-zinc-200 dark:border-zinc-800 px-4 py-4">
          {/* Brand Header */}
          <div className="flex items-center justify-between mb-4">
            <span className="text-xl font-semibold text-blue-600">AS13.ai</span>
            <div className="md:hidden">
              <button
                onClick={toggleSidebar}
                className="text-gray-500 dark:text-gray-300 hover:text-white"
                aria-label="Close Sidebar"
              >
                <X className="w-5 h-5" />
              </button>
            </div>
          </div>

          {/* New Chat */}
          <button
            onClick={handleNewChat}
            className="w-full flex items-center gap-2 px-3 py-2 mb-4 bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 rounded-lg text-sm font-medium transition"
          >
            <Plus className="w-4 h-4" />
            New Chat
          </button>

          {/* Recent Chats */}
          <div className="mb-4">
            <p className="text-xs font-semibold text-zinc-500 dark:text-zinc-400 mb-2">
              Recent
            </p>
            <div className="space-y-2">
              {[...Array(3)].map((_, idx) => (
                <div
                  key={idx}
                  className="px-3 py-2 rounded-lg bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 text-sm cursor-pointer transition"
                >
                  Chat session {idx + 1}
                </div>
              ))}
            </div>
          </div>

          {/* Footer Actions */}
          <div className="border-t border-zinc-200 dark:border-zinc-800 pt-4 space-y-2">
            <button className="w-full flex items-center gap-2 px-3 py-2 rounded-lg hover:bg-zinc-100 dark:hover:bg-zinc-800 transition text-sm">
              <Sparkles className="w-4 h-4 text-yellow-400" />
              Upgrade to Pro
            </button>

            <button
              onClick={goToSettings}
              className="w-full flex items-center gap-2 px-3 py-2 rounded-lg hover:bg-zinc-100 dark:hover:bg-zinc-800 transition text-sm"
            >
              <Settings className="w-4 h-4" />
              Settings
            </button>

            <button
              onClick={handleLogout}
              className="w-full flex items-center gap-2 px-3 py-2 text-red-500 hover:bg-red-100 dark:hover:bg-red-950 rounded-lg transition text-sm"
            >
              <LogOut className="w-4 h-4" />
              Logout
            </button>

            {/* Theme Toggle */}
            <button
              onClick={() => setDark(!dark)}
              className="w-full flex items-center gap-2 px-3 py-2 bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 rounded-lg transition text-sm"
              title={dark ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
            >
              {dark ? (
                <>
                  <Sun className="w-4 h-4 text-yellow-400" />
                  Light Mode
                </>
              ) : (
                <>
                  <Moon className="w-4 h-4" />
                  Dark Mode
                </>
              )}
            </button>
          </div>
        </aside>
      </div>
    </div>
  );
}
