import { useState } from "react";
import { Menu, X, Plus, LogOut, Settings } from "lucide-react"; // Install: npm install lucide-react

export default function Sidebar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  return (
    <>
      {/* Hamburger Button */}
      <div className="fixed top-4 left-4 z-50 md:hidden">
        <button
          onClick={toggleSidebar}
          className="text-white bg-zinc-900 p-2 rounded-full shadow hover:bg-zinc-800 transition"
        >
          {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
        </button>
      </div>

      {/* Backdrop (mobile) */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
          onClick={toggleSidebar}
        />
      )}

      {/* Sidebar Drawer */}
      <div
        className={`fixed top-0 left-0 z-50 h-full w-64 bg-zinc-900 text-white shadow-lg transform transition-transform duration-300 ease-in-out
        ${isOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 md:static md:block`}
      >
        <div className="flex flex-col h-full p-4">
          {/* Logo */}
          <div className="flex items-center justify-between mb-6">
            <h1 className="text-lg font-bold text-blue-400">ðŸ§  AS13.ai</h1>
            <button onClick={toggleSidebar} className="md:hidden">
              <X className="w-5 h-5 text-gray-400" />
            </button>
          </div>

          {/* New Chat */}
          <button className="flex items-center gap-2 px-4 py-2 bg-zinc-800 hover:bg-zinc-700 rounded-lg text-sm font-medium transition">
            <Plus className="w-4 h-4" /> New Chat
          </button>

          {/* Chat History Scrollable */}
          <div className="mt-6 flex-1 overflow-y-auto scrollbar-thin scrollbar-thumb-zinc-700 scrollbar-track-zinc-800">
            <p className="text-sm text-gray-400 uppercase mb-2">Recent</p>
            <div className="text-gray-300 italic text-sm">No conversations yet</div>
          </div>

          {/* Footer - Settings / Logout */}
          <div className="mt-6 border-t border-gray-700 pt-4 space-y-2">
            <button className="flex items-center gap-2 w-full px-3 py-2 hover:bg-zinc-800 rounded-lg text-sm transition">
              <Settings className="w-4 h-4" /> Settings
            </button>
            <button className="flex items-center gap-2 w-full px-3 py-2 hover:bg-zinc-800 rounded-lg text-sm transition text-red-400">
              <LogOut className="w-4 h-4" /> Logout
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
