import { useState, useEffect, useRef } from 'react';
import { marked } from 'marked';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

export default function Chat() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem('messages');
    return saved ? JSON.parse(saved) : [];
  });

  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const bottomRef = useRef(null);
  const navigate = useNavigate();

  // ✅ Clear chat only on full page reload
  useEffect(() => {
    const isReload = !sessionStorage.getItem('visited');
    sessionStorage.setItem('visited', 'true');

    if (isReload) {
      setMessages([]);
      localStorage.removeItem('messages');
    }
  }, []);

  // ✅ Save messages to localStorage and scroll to bottom
  useEffect(() => {
    localStorage.setItem('messages', JSON.stringify(messages));
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = async (messageText = input) => {
    if (!messageText.trim()) return;

    setMessages((prev) => [...prev, { role: 'user', text: messageText }]);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageText }),
      });

      const data = await res.json();
      setMessages((prev) => [...prev, { role: 'ai', text: data.reply }]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { role: 'ai', text: '⚠️ Something went wrong. Please try again.' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const resetChat = () => {
    setMessages([]);
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited'); // to force reset on next mount
    navigate('/chat');
  };

  const suggestedPrompts = [
    'Build a todo app in React using Tailwind',
    'Build a simple blog using Astro',
    'Create a dark/light theme toggle in React',
    'Write a REST API using Node.js',
  ];

  return (
    <div className="flex flex-col min-h-screen bg-black text-white">
      <Header />

      <main className="flex-1 flex justify-center px-4 sm:px-8">
        {messages.length === 0 ? (
          <div className="flex flex-col justify-center items-center text-center max-w-xl mx-auto w-full py-20 sm:py-32">
            <h1 className="text-3xl sm:text-4xl font-extrabold mb-2">
              Where <span className="text-blue-400">ideas</span> begin
            </h1>
            <p className="text-gray-400 mb-6 text-sm sm:text-base">
              Bring ideas to life in seconds or get help with your projects.
            </p>

            <textarea
              rows={3}
              placeholder="How can AS13 help you today?"
              className="w-full p-3 rounded-md bg-zinc-900 border border-blue-500 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-600 resize-none"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  sendMessage();
                }
              }}
            />

            <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 gap-3 w-full">
              {suggestedPrompts.map((prompt, index) => (
                <button
                  key={index}
                  onClick={() => {
                    setInput(prompt);
                    sendMessage(prompt);
                  }}
                  className="px-4 py-3 rounded-full border border-zinc-700 bg-zinc-900 text-white hover:bg-zinc-800 transition text-sm sm:text-base text-left"
                >
                  {prompt}
                </button>
              ))}
            </div>
          </div>
        ) : (
          <div className="max-w-3xl mx-auto relative w-full py-10 sm:py-14">
            <button
              onClick={resetChat}
              className="absolute right-0 top-0 text-blue-400 border border-blue-500 rounded-md px-3 py-1 text-sm hover:bg-blue-600 hover:text-white transition"
            >
              + New Chat
            </button>

            <div className="space-y-4 pt-10">
              {messages.map((msg, i) => (
                <div
                  key={i}
                  className={`p-4 rounded-lg text-sm sm:text-base whitespace-pre-wrap ${
                    msg.role === 'user'
                      ? 'bg-blue-100 text-black'
                      : 'bg-zinc-800 text-white'
                  }`}
                  dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
                />
              ))}

              {loading && (
                <div className="p-4 rounded-lg bg-zinc-800 text-white text-sm italic animate-pulse">
                  AS13 is typing...
                </div>
              )}
              <div ref={bottomRef} />
            </div>
          </div>
        )}
      </main>

      {messages.length > 0 && (
        <div className="bg-zinc-900 p-4 border-t border-zinc-700">
          <div className="max-w-3xl mx-auto flex gap-2">
            <textarea
              rows={2}
              placeholder="Ask anything..."
              className="flex-1 p-3 rounded-md bg-zinc-800 border border-zinc-600 text-white resize-none"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  sendMessage();
                }
              }}
            />
            {input.trim() && (
              <button
                onClick={() => sendMessage()}
                className="bg-blue-600 hover:bg-blue-700 px-5 py-2 rounded-md text-white font-medium"
              >
                Send
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
