import { useState, useEffect } from 'react';
import { marked } from 'marked';

export default function Chat() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem("messages");
    return saved ? JSON.parse(saved) : [];
  });
  const [input, setInput] = useState('');

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const res = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: input }),
    });
    const data = await res.json();
    setMessages([
      ...messages,
      { role: 'user', text: input },
      { role: 'ai', text: data.reply }
    ]);
    setInput('');
  };

  return (
    <div className="flex flex-col min-h-screen bg-black text-white">
      {/* Header */}
      <div className="text-center p-8">
        <h1 className="text-4xl font-bold text-white">Where ideas begin</h1>
        <p className="text-gray-400 mt-2">Bring ideas to life in seconds or get help on existing projects.</p>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto px-4 py-6 max-w-2xl mx-auto w-full space-y-4">
        {messages.map((msg, i) => (
          <div
            key={i}
            className={`p-4 rounded-lg text-sm sm:text-base whitespace-pre-wrap ${
              msg.role === 'user'
                ? 'bg-blue-100 text-black'
                : 'bg-zinc-800 text-white'
            }`}
            dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
          />
        ))}
      </div>

      {/* Input */}
      <div className="bg-zinc-900 p-4 border-t border-zinc-800">
        <div className="max-w-2xl mx-auto flex flex-col sm:flex-row gap-3 w-full">
          <textarea
            rows={2}
            placeholder="How can AS13 help you today?"
            className="flex-1 p-3 rounded-md bg-zinc-800 border border-zinc-600 text-white resize-none"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button
            onClick={sendMessage}
            className="bg-blue-600 hover:bg-blue-700 px-5 py-2 rounded-md text-white font-medium"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
