import { useState, useEffect, useRef } from 'react';
import { marked } from 'marked';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Sidebar from './Sidebar';

export default function Chat({ dark, setDark }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [initializing, setInitializing] = useState(true);
  const bottomRef = useRef(null);
  const inputRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const applyTheme = () => {
      const isDark = localStorage.getItem('theme') === 'dark';
      document.documentElement.classList.toggle('dark', isDark);
    };
    applyTheme();
    window.addEventListener('storage', applyTheme);
    return () => window.removeEventListener('storage', applyTheme);
  }, []);

  useEffect(() => {
    const isReload = !sessionStorage.getItem('visited');
    sessionStorage.setItem('visited', 'true');
    if (isReload) {
      localStorage.removeItem('messages');
      setMessages([]);
    } else {
      const saved = localStorage.getItem('messages');
      if (saved) setMessages(JSON.parse(saved));
    }
    setInitializing(false);
  }, []);

  useEffect(() => {
    localStorage.setItem('messages', JSON.stringify(messages));
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = async (messageText) => {
    const text = typeof messageText === 'string' ? messageText : input;
    if (!text.trim()) return;

    setMessages((prev) => [...prev, { role: 'user', text }]);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: text }),
      });

      const data = await res.json();
      setMessages((prev) => [...prev, { role: 'ai', text: data.reply }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { role: 'ai', text: '⚠️ Something went wrong. Please try again.' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const suggestedPrompts = [
    'Build a todo app in React using Tailwind',
    'Build a simple blog using Astro',
    'Create a dark/light theme toggle in React',
    'Write a REST API using Node.js',
  ];

  if (initializing) return null;

  return (
    <div className="flex flex-col min-h-screen bg-white dark:bg-black text-black dark:text-white">
      <Header />
      <div className="flex flex-1">
        <Sidebar dark={dark} setDark={setDark} />
        <div className="flex-1 flex flex-col px-4 sm:px-6">
          <div className="flex-1 overflow-y-auto pt-4">
            {messages.length === 0 ? (
              <div className="text-center max-w-xl mx-auto pt-10 pb-16">
                <h1 className="text-3xl font-bold mb-2">
                  Where <span className="text-blue-500">ideas</span> begin
                </h1>
                <p className="text-gray-600 dark:text-gray-400 mb-6 text-sm">
                  Bring ideas to life in seconds or get help with your projects.
                </p>
                <div className="relative mb-8">
                  <textarea
                    ref={inputRef}
                    rows={3}
                    placeholder="How can AS13 help you today?"
                    className="w-full p-3 pr-12 rounded-lg bg-zinc-100 dark:bg-zinc-900 border border-blue-600 text-sm resize-none text-black dark:text-white"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                  />
                  <button
                    onClick={sendMessage}
                    disabled={!input.trim()}
                    className="absolute top-2.5 right-2.5 w-9 h-9 bg-blue-600 hover:bg-blue-700 disabled:bg-zinc-700 rounded-md flex items-center justify-center text-white"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
                      <path strokeLinecap="round" strokeLinejoin="round" d="M4 4l16 8-16 8V4z" />
                    </svg>
                  </button>
                </div>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                  {suggestedPrompts.map((prompt, index) => (
                    <button
                      key={index}
                      onClick={() => sendMessage(prompt)}
                      className="px-4 py-3 text-left text-sm rounded-md border bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 transition"
                    >
                      {prompt}
                    </button>
                  ))}
                </div>
              </div>
            ) : (
              <div className="space-y-3 px-1 sm:px-2 pb-24">
                {messages.map((msg, i) => (
                  <div
                    key={i}
                    className={`p-3 rounded-lg text-sm whitespace-pre-wrap ${
                      msg.role === 'user'
                        ? 'bg-blue-100 text-black'
                        : 'bg-zinc-800 text-white'
                    }`}
                    dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
                  />
                ))}
                {loading && (
                  <div className="p-3 rounded-lg bg-zinc-800 text-white text-sm italic animate-pulse">
                    AS13 is typing...
                  </div>
                )}
                <div ref={bottomRef} />
              </div>
            )}
          </div>
          {messages.length > 0 && (
            <div className="py-4 px-2 bg-white dark:bg-black sticky bottom-0 w-full">
              <div className="max-w-2xl mx-auto relative">
                <textarea
                  ref={inputRef}
                  rows={2}
                  placeholder="Ask anything..."
                  className="w-full p-3 pr-12 rounded-lg bg-zinc-100 dark:bg-zinc-800 border border-zinc-300 dark:border-zinc-700 text-sm resize-none text-black dark:text-white"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      sendMessage();
                    }
                  }}
                />
                <button
                  onClick={sendMessage}
                  disabled={!input.trim()}
                  className="absolute top-2.5 right-2 w-9 h-9 bg-blue-600 hover:bg-blue-700 disabled:bg-zinc-700 rounded-md flex items-center justify-center text-white"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
                    <path strokeLinecap="round" strokeLinejoin="round" d="M4 4l16 8-16 8V4z" />
                  </svg>
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
