import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import logoText from '../icons/logo-text.svg';
import googleIcon from '../icons/google.svg';
import microsoftIcon from '../icons/microsoft.svg';
import Header from '../components/Header';

export default function Login() {
  const { login } = useContext(AuthContext);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5001/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });

      if (!res.ok) {
        const { error } = await res.json();
        throw new Error(error || 'Login failed');
      }

      const data = await res.json();
      login(data);
      navigate('/chat');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <>
      <Header />
      <div className="flex justify-center items-center h-screen bg-zinc-100 dark:bg-zinc-900">
        <form onSubmit={handleSubmit} className="bg-white dark:bg-zinc-800 p-8 rounded shadow-md w-80">
          <img src={logoText} alt="Logo" className="mb-6 h-6 mx-auto" />
          <h2 className="text-center text-xl font-semibold mb-4">Welcome back</h2>
          {error && <p className="text-red-500 text-sm mb-3">{error}</p>}
          <input
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-2 mb-3 rounded border dark:bg-zinc-700"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 mb-4 rounded border dark:bg-zinc-700"
            required
          />
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
            Login
          </button>
          <div className="my-4 text-center text-sm text-gray-500 dark:text-gray-300">or</div>
          <a
            href="http://localhost:5001/auth/google?state=login"
            className="flex items-center justify-center gap-3 w-full text-center bg-white dark:bg-zinc-700 text-black dark:text-white py-2 rounded-md border hover:bg-gray-100 dark:hover:bg-zinc-600 mb-2"
          >
            <img src={googleIcon} alt="Google" className="h-5" />
            Continue with Google
          </a>
          <a
            href="http://localhost:5001/auth/microsoft?state=login"
            className="flex items-center justify-center gap-3 w-full text-center bg-blue-700 text-white py-2 rounded-md hover:bg-blue-800"
          >
            <img src={microsoftIcon} alt="Microsoft" className="h-5" />
            Continue with Microsoft
          </a>
        </form>
      </div>
    </>
  );
}
