import { useLocation, useNavigate } from 'react-router-dom';
import logo from '../icons/logo.svg';
import Sidebar from './Sidebar';

export default function Header() {
  const location = useLocation();
  const navigate = useNavigate();
  const isChatPage = location.pathname === '/chat';

  const handleLogoClick = () => {
    // Clear stored messages
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited'); // To simulate fresh session
    navigate('/chat');
  };

  return (
    <header className="bg-zinc-900 text-white p-4 shadow-md fixed top-0 w-full z-50 flex items-center justify-between">
      {/* Logo - triggers new chat */}
      <button
        onClick={handleLogoClick}
        className="flex items-center space-x-2 hover:opacity-80 transition-opacity cursor-pointer pl-2"
      >
        <img src={logo} alt="AS13.ai Logo" className="h-8 w-auto" />
        <span className="text-xl font-bold text-blue-500">AS13.ai</span>
      </button>

      {/* Sidebar toggle only on chat page */}
      {isChatPage && <Sidebar />}
    </header>
  );
}
