import { useState, useEffect } from 'react';
import { marked } from 'marked';
import Header from './Header'; // if you want to reuse it at top

export default function Chat() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem("messages");
    return saved ? JSON.parse(saved) : [];
  });
  const [input, setInput] = useState('');

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const res = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: input }),
    });
    const data = await res.json();
    setMessages([
      ...messages,
      { role: 'user', text: input },
      { role: 'ai', text: data.reply }
    ]);
    setInput('');
  };

  return (
    <div className="flex flex-col h-screen bg-black text-white">
      <Header />
      <main className="flex-1 overflow-y-auto p-4 sm:p-10">
        <div className="max-w-3xl mx-auto">
          <div className="text-center mb-8">
            <h1 className="text-3xl sm:text-4xl font-bold">Where ideas begin</h1>
            <p className="text-gray-400 mt-2 text-sm sm:text-base">Bring ideas to life in seconds...</p>
          </div>

          <div className="space-y-4">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`p-4 rounded-lg text-sm sm:text-base whitespace-pre-wrap ${
                  msg.role === 'user'
                    ? 'bg-blue-100 text-black'
                    : 'bg-zinc-800 text-white'
                }`}
                dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
              />
            ))}
          </div>
        </div>
      </main>

      <div className="bg-zinc-900 p-4 border-t border-zinc-700">
        <div className="max-w-3xl mx-auto flex gap-2">
          <textarea
            rows={2}
            placeholder="Ask anything..."
            className="flex-1 p-3 rounded-md bg-zinc-800 border border-zinc-600 text-white resize-none"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button
            onClick={sendMessage}
            className="bg-blue-600 hover:bg-blue-700 px-5 py-2 rounded-md text-white font-medium"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
