import { createContext, useState, useContext, useEffect } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // On app start: load from localStorage or validate session with backend
  useEffect(() => {
    const storedUser = localStorage.getItem('user');

    if (storedUser) {
      setUser(JSON.parse(storedUser));
      setLoading(false);
    } else {
      // Check for OAuth/session-based login
      fetch('http://localhost:5001/api/me', {
        method: 'GET',
        credentials: 'include', // ðŸ‘ˆ Send cookies
      })
        .then((res) => {
          if (!res.ok) throw new Error('Not authenticated');
          return res.json();
        })
        .then((data) => {
          setUser(data);
          localStorage.setItem('user', JSON.stringify(data));
        })
        .catch(() => {
          setUser(null);
          localStorage.removeItem('user');
        })
        .finally(() => setLoading(false));
    }
  }, []);

  const login = (dUser) => {
    setUser(dUser);
    localStorage.setItem('user', JSON.stringify(dUser));
  };

  const logout = async () => {
    try {
      await fetch('http://localhost:5001/api/logout', {
        method: 'GET',
        credentials: 'include', // ðŸ‘ˆ Tell backend to destroy session
      });
    } catch (err) {
      console.error('Logout failed', err);
    } finally {
      setUser(null);
      localStorage.removeItem('user');
    }
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
