import { useState, useEffect, useRef } from 'react';
import { marked } from 'marked';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

export default function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [initializing, setInitializing] = useState(true);
  const bottomRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const isReload = !sessionStorage.getItem('visited');
    sessionStorage.setItem('visited', 'true');

    if (isReload) {
      localStorage.removeItem('messages');
      setMessages([]);
    } else {
      const saved = localStorage.getItem('messages');
      if (saved) setMessages(JSON.parse(saved));
    }

    setInitializing(false);
  }, []);

  useEffect(() => {
    localStorage.setItem('messages', JSON.stringify(messages));
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = async (messageText = input) => {
    if (!messageText.trim()) return;

    setMessages((prev) => [...prev, { role: 'user', text: messageText }]);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageText }),
      });

      const data = await res.json();
      setMessages((prev) => [...prev, { role: 'ai', text: data.reply }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { role: 'ai', text: '⚠️ Something went wrong. Please try again.' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const resetChat = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
  };

  const suggestedPrompts = [
    'Build a todo app in React using Tailwind',
    'Build a simple blog using Astro',
    'Create a dark/light theme toggle in React',
    'Write a REST API using Node.js',
  ];

  if (initializing) return null;

  return (
    <div className="flex flex-col min-h-screen bg-black text-white">
      <Header />

      <main className="flex-1 flex flex-col items-center px-4 sm:px-8 pt-20">
        {messages.length === 0 ? (
          <div className="text-center max-w-xl w-full py-16 sm:py-24">
            <h1 className="text-4xl font-extrabold mb-2">
              Where <span className="text-blue-400">ideas</span> begin
            </h1>
            <p className="text-gray-400 mb-6 text-sm sm:text-base">
              Bring ideas to life in seconds or get help with your projects.
            </p>

            <div className="w-full space-y-3">
              <textarea
                rows={3}
                placeholder="How can AS13 help you today?"
                className="w-full p-4 rounded-xl bg-zinc-900 border border-blue-600 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-600 resize-none"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    sendMessage();
                  }
                }}
              />
              <div className="flex justify-end">
                {input.trim() && (
                  <button
                    onClick={sendMessage}
                    className="bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded-xl text-white font-semibold transition"
                  >
                    Send
                  </button>
                )}
              </div>
            </div>

            <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 gap-4">
              {suggestedPrompts.map((prompt, index) => (
                <button
                  key={index}
                  onClick={() => {
                    setInput(prompt);
                    sendMessage(prompt);
                  }}
                  className="text-left px-5 py-4 rounded-xl bg-zinc-900 border border-zinc-700 hover:bg-zinc-800 transition text-sm sm:text-base font-medium"
                >
                  {prompt}
                </button>
              ))}
            </div>
          </div>
        ) : (
          <div className="max-w-3xl w-full relative py-10 sm:py-14">
            <div className="flex justify-end mb-6">
              <button
                onClick={resetChat}
                className="text-blue-400 border border-blue-500 rounded-md px-4 py-2 text-sm hover:bg-blue-600 hover:text-white transition"
              >
                + New Chat
              </button>
            </div>

            <div className="space-y-4">
              {messages.map((msg, i) => (
                <div
                  key={i}
                  className={`p-4 rounded-xl text-sm sm:text-base whitespace-pre-wrap ${
                    msg.role === 'user'
                      ? 'bg-blue-100 text-black'
                      : 'bg-zinc-800 text-white'
                  }`}
                  dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
                />
              ))}

              {loading && (
                <div className="p-4 rounded-xl bg-zinc-800 text-white text-sm italic animate-pulse">
                  AS13 is typing...
                </div>
              )}
              <div ref={bottomRef} />
            </div>
          </div>
        )}
      </main>

      {messages.length > 0 && (
        <div className="bg-zinc-900 border-t border-zinc-800 py-6 px-4">
          <div className="max-w-2xl mx-auto flex items-end gap-3">
            <textarea
              rows={2}
              placeholder="Ask anything..."
              className="flex-1 p-4 rounded-xl bg-zinc-800 border border-zinc-700 text-white placeholder-gray-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-600 transition"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  sendMessage();
                }
              }}
            />
            <button
              onClick={sendMessage}
              disabled={!input.trim()}
              className="shrink-0 bg-blue-600 hover:bg-blue-700 disabled:bg-zinc-700 px-5 py-3 rounded-xl text-white font-semibold transition"
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
