import { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Plus,
  LogOut,
  Settings,
  Sparkles,
  Sun,
  Moon
} from 'lucide-react';
import { AuthContext } from '../context/AuthContext';

export default function Sidebar({ dark, setDark }) {
  const navigate = useNavigate();
  const { logout } = useContext(AuthContext);

  const handleNewChat = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
  };

  const handleLogout = () => {
    logout();
    alert('You have logged out.');
    navigate('/login');
  };

  const goToSettings = () => {
    navigate('/settings');
  };

  return (
    <div className="w-64 h-full bg-white dark:bg-zinc-900 text-black dark:text-white flex flex-col shadow-lg">
      {/* Header */}
      <div className="p-4 border-b border-zinc-300 dark:border-zinc-800">
        <span className="text-blue-500 text-xl font-bold">AS13.ai</span>
      </div>

      {/* New Chat */}
      <div className="p-4">
        <button
          onClick={handleNewChat}
          className="w-full flex items-center gap-2 px-4 py-2 text-sm font-medium bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 rounded-lg transition"
        >
          <Plus className="w-4 h-4" />
          New Chat
        </button>
      </div>

      {/* Recent Chats */}
      <div className="flex-1 overflow-y-auto px-4 pb-4 space-y-2 scrollbar-thin scrollbar-thumb-zinc-700 scrollbar-track-transparent">
        <p className="text-gray-600 dark:text-gray-400 text-xs uppercase mb-2 tracking-wide">
          Recent Chats
        </p>
        {[...Array(1)].map((_, idx) => (
          <div
            key={idx}
            className="bg-zinc-100 dark:bg-zinc-800 px-4 py-2 rounded-lg text-sm hover:bg-zinc-200 dark:hover:bg-zinc-700 cursor-pointer transition"
          >
            Chat session {idx + 1}
          </div>
        ))}
      </div>

      {/* Footer */}
      <div className="p-4 border-t border-zinc-300 dark:border-zinc-800 space-y-2">
        <button className="w-full flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-zinc-100 dark:hover:bg-zinc-800 transition text-sm">
          <Sparkles className="w-4 h-4 text-yellow-400" />
          Upgrade Plan
        </button>
        <button
          onClick={goToSettings}
          className="w-full flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-zinc-100 dark:hover:bg-zinc-800 transition text-sm"
        >
          <Settings className="w-4 h-4" />
          Settings
        </button>
        <button
          onClick={handleLogout}
          className="w-full flex items-center gap-2 px-4 py-2 rounded-lg text-red-500 dark:hover:bg-red-950 hover:bg-red-100 transition text-sm"
        >
          <LogOut className="w-4 h-4" />
          Logout
        </button>

        {/* Theme Toggle */}
        <button
          onClick={() => setDark(!dark)}
          className="w-full flex items-center justify-between px-4 py-2 mt-2 bg-zinc-100 dark:bg-zinc-800 hover:bg-zinc-200 dark:hover:bg-zinc-700 rounded-lg transition text-sm"
          title={dark ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
        >
          <span className="flex items-center gap-2">
            {dark ? (
              <Sun className="w-4 h-4 text-yellow-400" />
            ) : (
              <Moon className="w-4 h-4" />
            )}
            {dark ? 'Light Mode' : 'Dark Mode'}
          </span>
        </button>
      </div>
    </div>
  );
}
