import { useState, useEffect } from 'react';
import { marked } from 'marked';
import Header from './Header';

export default function Chat() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem("messages");
    return saved ? JSON.parse(saved) : [];
  });
  const [input, setInput] = useState('');

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const sendMessage = async (messageText = input) => {
    if (!messageText.trim()) return;
    const res = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: messageText }),
    });
    const data = await res.json();
    setMessages([
      ...messages,
      { role: 'user', text: messageText },
      { role: 'ai', text: data.reply }
    ]);
    setInput('');
  };

  const suggestedPrompts = [
    "Build a todo app in React using Tailwind",
    "Build a simple blog using Astro"
  ];

  return (
    <div className="flex flex-col min-h-screen bg-black text-white">
      <Header />

      <main className="flex-1 flex flex-col items-center justify-center px-4 py-12 sm:py-20 text-center">
        {messages.length === 0 ? (
          <>
            <h1 className="text-3xl sm:text-4xl font-extrabold mb-2">
              <span className="text-white">Where</span>{' '}
              <span className="text-white font-bold">ideas begin</span>
            </h1>
            <p className="text-gray-400 mb-8 text-sm sm:text-base">
              Bring ideas to life in seconds or get help on existing projects.
            </p>

            <textarea
              rows={3}
              placeholder="How can AS13 help you today?"
              className="w-full max-w-xl p-3 rounded-md bg-zinc-900 border border-blue-500 text-white placeholder-gray-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />

            <div className="mt-4 space-y-2 w-full max-w-xl">
              {suggestedPrompts.map((prompt, index) => (
                <button
                  key={index}
                  onClick={() => sendMessage(prompt)}
                  className="w-full text-left px-4 py-3 border border-blue-500 rounded-md hover:bg-zinc-800 transition flex justify-between items-center text-sm sm:text-base"
                >
                  {prompt}
                  <span className="text-blue-400">↩</span>
                </button>
              ))}
            </div>
          </>
        ) : (
          <div className="w-full max-w-3xl px-4 space-y-4">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`p-4 rounded-lg text-sm sm:text-base whitespace-pre-wrap ${
                  msg.role === 'user'
                    ? 'bg-blue-100 text-black'
                    : 'bg-zinc-800 text-white'
                }`}
                dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
              />
            ))}
          </div>
        )}
      </main>

      {messages.length > 0 && (
        <div className="bg-zinc-900 p-4 border-t border-zinc-700">
          <div className="max-w-3xl mx-auto flex gap-2">
            <textarea
              rows={2}
              placeholder="Ask anything..."
              className="flex-1 p-3 rounded-md bg-zinc-800 border border-zinc-600 text-white resize-none"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button
              onClick={() => sendMessage()}
              className="bg-blue-600 hover:bg-blue-700 px-5 py-2 rounded-md text-white font-medium"
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
