import { useEffect, useState } from 'react';
import { useStore } from 'nanostores/react';
import { themeStore, toggleTheme } from '../stores/theme'; // Adjust if needed

export default function ThemeToggle() {
  const theme = useStore(themeStore);
  const [dark, setDark] = useState(theme === 'dark');

  useEffect(() => {
    document.documentElement.classList.toggle('dark', dark);
  }, [dark]);

  const handleToggle = () => {
    toggleTheme();
    setDark((prev) => !prev);
  };

  return (
    <div className="fixed bottom-6 right-6 z-50">
      <button
        onClick={handleToggle}
        className="bg-zinc-900 hover:bg-zinc-800 text-white dark:text-yellow-300 p-3 rounded-full shadow-lg transition-all duration-300 focus:outline-none"
        title={dark ? 'Light Mode' : 'Dark Mode'}
      >
        {dark ? 'ðŸŒž' : 'ðŸŒ™'}
      </button>
    </div>
  );
}
