import { useState } from "react";
import { Menu, X } from "lucide-react"; // Make sure you installed this via `npm install lucide-react`

export default function Sidebar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  return (
    <>
      {/* Toggle Button */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <button
          onClick={toggleSidebar}
          className="text-white bg-zinc-900 p-2 rounded-full shadow-md hover:bg-zinc-800"
        >
          {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
        </button>
      </div>

      {/* Backdrop on mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black opacity-30 z-30 md:hidden"
          onClick={toggleSidebar}
        />
      )}

      {/* Sidebar Drawer */}
      <div
        className={`fixed top-0 left-0 z-40 h-full w-64 bg-zinc-900 text-white transform transition-transform duration-300 ease-in-out
        ${isOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 md:static md:block`}
      >
        <div className="p-5">
          <h1 className="text-xl font-bold text-blue-400 mb-6">ðŸ§  AS13.ai</h1>

          <button className="w-full text-left px-4 py-2 bg-zinc-800 hover:bg-zinc-700 rounded-lg transition">
            âž• Start new chat
          </button>

          <div className="mt-6">
            <p className="text-sm text-gray-400 uppercase">Your Chats</p>
            <div className="text-gray-300 mt-3 italic">No previous conversations</div>
          </div>
        </div>
      </div>
    </>
  );
}
