import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import logo from '../icons/logo-text.svg';

export default function Signup() {
  const [form, setForm] = useState({ email: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const email = form.email.trim();
    const password = form.password.trim();
    const confirmPassword = form.confirmPassword.trim();

    if (!email || !password || !confirmPassword) {
      setError('All fields are required.');
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    try {
      const res = await fetch('http://localhost:5001/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();
      if (!res.ok) {
        setError(data.message || 'Signup failed');
        return;
      }

      alert('Signup successful!');
      navigate('/chat');
    } catch (err) {
      console.error('Signup error:', err);
      setError('Server error. Please try again later.');
    }
  };

  return (
    <>
      <Header />
      <main className="min-h-screen flex items-center justify-center px-4 bg-gray-950">
        <div className="w-full max-w-md">
          <div className="bg-gray-900 border border-gray-700 rounded-3xl px-8 py-12 shadow-2xl">
            <div className="text-center mb-8">
              <img src={logo} alt="AS13.ai" className="mx-auto h-10 w-auto" />
              <h2 className="mt-4 text-3xl font-bold text-white">Create your account</h2>
            </div>

            <form onSubmit={handleSubmit} className="space-y-5">
              {error && <p className="text-sm text-red-400 text-center">{error}</p>}
              <input
                type="email"
                placeholder="you@example.com"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
                className="auth-input"
              />
              <input
                type="password"
                placeholder="Password"
                value={form.password}
                onChange={(e) => setForm({ ...form, password: e.target.value })}
                className="auth-input"
              />
              <input
                type="password"
                placeholder="Confirm Password"
                value={form.confirmPassword}
                onChange={(e) => setForm({ ...form, confirmPassword: e.target.value })}
                className="auth-input"
              />
              <button
                type="submit"
                className="w-full bg-blue-600 hover:bg-blue-700 py-3 rounded-xl text-white font-semibold transition"
              >
                Sign up
              </button>
            </form>

            <div className="mt-6 space-y-3">
              <a
                href="http://localhost:5001/auth/google"
                className="oauth-btn bg-white text-black hover:bg-gray-100"
              >
                Sign up with Google
              </a>
              <a
                href="http://localhost:5001/auth/microsoft"
                className="oauth-btn bg-blue-700 text-white hover:bg-blue-800"
              >
                Sign up with Microsoft
              </a>
            </div>

            <p className="mt-6 text-center text-sm text-gray-400">
              Already have an account?{' '}
              <a href="/login" className="text-blue-500 hover:underline">Log in</a>
            </p>
          </div>
        </div>
      </main>
    </>
  );
}
