import { useState, useEffect, useRef } from 'react';
import { marked } from 'marked';
import Header from './Header';

export default function Chat() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem("messages");
    return saved ? JSON.parse(saved) : [];
  });
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const bottomRef = useRef(null);

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = async (messageText = input) => {
    if (!messageText.trim()) return;
    setMessages(prev => [...prev, { role: 'user', text: messageText }]);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageText }),
      });
      const data = await res.json();
      setMessages(prev => [...prev, { role: 'ai', text: data.reply }]);
    } catch {
      setMessages(prev => [...prev, { role: 'ai', text: '⚠️ Something went wrong. Try again.' }]);
    } finally {
      setLoading(false);
    }
  };

  const resetChat = () => {
    setMessages([]);
    localStorage.removeItem('messages');
  };

  const suggestedPrompts = [
    "Build a todo app in React using Tailwind",
    "Build a simple blog using Astro",
  ];

  return (
    <div className="flex flex-col h-screen bg-black text-white">
      <Header />

      <main className="flex-1 overflow-y-auto px-4 sm:px-8 py-6 sm:py-10">
        {messages.length === 0 ? (
          <div className="max-w-xl mx-auto text-center">
            <h1 className="text-3xl sm:text-4xl font-extrabold mb-2">
              Where <span className="text-blue-400">ideas</span> begin
            </h1>
            <p className="text-gray-400 mb-6 text-sm sm:text-base">
              Bring ideas to life in seconds or get help with your projects.
            </p>

            <textarea
              rows={3}
              placeholder="How can AS13 help you today?"
              className="w-full p-3 rounded-md bg-zinc-900 border border-blue-500 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-600 resize-none"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  sendMessage();
                }
              }}
            />

            <div className="mt-4 space-y-2">
              {suggestedPrompts.map((prompt, index) => (
                <button
                  key={index}
                  onClick={() => sendMessage(prompt)}
                  className="w-full text-left px-4 py-3 border border-zinc-700 rounded-md hover:bg-zinc-800 transition flex justify-between items-center text-sm"
                >
                  {prompt}
                  <span className="text-blue-400">↩</span>
                </button>
              ))}
            </div>
          </div>
        ) : (
          <div className="max-w-3xl mx-auto relative">
            <button
              onClick={resetChat}
              className="absolute right-0 top-0 text-red-400 text-sm hover:underline"
            >
              Reset Chat ✖
            </button>

            <div className="space-y-4 pt-8">
              {messages.map((msg, i) => (
                <div
                  key={i}
                  className={`p-4 rounded-lg text-sm sm:text-base whitespace-pre-wrap ${
                    msg.role === 'user'
                      ? 'bg-blue-100 text-black'
                      : 'bg-zinc-800 text-white'
                  }`}
                  dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
                />
              ))}

              {loading && (
                <div className="p-4 rounded-lg bg-zinc-800 text-white text-sm italic animate-pulse">
                  AS13 is typing...
                </div>
              )}

              <div ref={bottomRef} />
            </div>
          </div>
        )}
      </main>

      {/* Input Box Sticky Bottom */}
      {messages.length > 0 && (
        <div className="bg-zinc-950 px-4 sm:px-8 py-4 border-t border-zinc-700">
          <div className="max-w-3xl mx-auto flex gap-2">
            <textarea
              rows={2}
              placeholder="Ask anything..."
              className="flex-1 p-3 rounded-md bg-zinc-900 border border-zinc-600 text-white resize-none focus:outline-none focus:ring-1 focus:ring-blue-500"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  sendMessage();
                }
              }}
            />
            {input.trim() && (
              <button
                onClick={() => sendMessage()}
                className="bg-blue-600 hover:bg-blue-700 px-5 py-2 rounded-md text-white font-medium"
              >
                Send
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
