import { useState } from "react";
import {
  Menu,
  X,
  Plus,
  LogOut,
  Settings,
  Sparkles
} from "lucide-react";

export default function Sidebar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  return (
    <>
      {/* Hamburger for mobile */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <button
          onClick={toggleSidebar}
          className="text-white bg-zinc-900 p-2 rounded-full shadow hover:bg-zinc-800"
        >
          {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
        </button>
      </div>

      {/* Backdrop on mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
          onClick={toggleSidebar}
        />
      )}

      {/* Sidebar Panel */}
      <div
        className={`fixed top-0 left-0 h-screen w-64 bg-zinc-900 text-white flex flex-col z-50 shadow-lg transform transition-transform duration-300 ease-in-out
        ${isOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 md:static md:block`}
      >
        {/* Header */}
        <div className="p-4 border-b border-zinc-700">
          <div className="flex justify-between items-center mb-4">
            <h1 className="text-blue-400 text-lg font-bold">ðŸ§  AS13.ai</h1>
            <button
              onClick={toggleSidebar}
              className="md:hidden text-gray-400 hover:text-white"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
          <button className="w-full flex items-center gap-2 px-4 py-2 text-sm font-medium bg-zinc-800 hover:bg-zinc-700 rounded-lg transition">
            <Plus className="w-4 h-4" /> New Chat
          </button>
        </div>

        {/* Chat History */}
        <div className="flex-1 overflow-y-auto px-4 py-4 space-y-3 scrollbar-thin scrollbar-thumb-zinc-700 scrollbar-track-zinc-800">
          <p className="text-gray-400 text-xs uppercase tracking-wide">Recent Chats</p>
          {[...Array(12)].map((_, idx) => (
            <div
              key={idx}
              className="bg-zinc-800 px-4 py-2 text-sm rounded-lg hover:bg-zinc-700 cursor-pointer transition"
            >
              Chat session {idx + 1}
            </div>
          ))}
        </div>

        {/* Footer Actions */}
        <div className="p-4 border-t border-zinc-700 space-y-2">
          <button className="w-full flex items-center gap-2 px-4 py-2 hover:bg-zinc-800 rounded-lg text-sm transition">
            <Sparkles className="w-4 h-4 text-yellow-400" />
            Upgrade Plan
          </button>
          <button className="w-full flex items-center gap-2 px-4 py-2 hover:bg-zinc-800 rounded-lg text-sm transition">
            <Settings className="w-4 h-4" />
            Settings
          </button>
          <button className="w-full flex items-center gap-2 px-4 py-2 hover:bg-zinc-800 rounded-lg text-sm text-red-400 transition">
            <LogOut className="w-4 h-4" />
            Logout
          </button>
        </div>
      </div>
    </>
  );
}
