import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import logoText from '../icons/logo-text.svg';
import googleIcon from '../icons/google.svg';
import microsoftIcon from '../icons/microsoft.svg';
import Header from '../components/Header';

export default function Signup() {
  const [form, setForm] = useState({ email: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email, password, confirmPassword } = form;
    if (!email || !password || !confirmPassword) {
      setError('All fields are required');
      return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const res = await fetch('http://localhost:5001/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Signup failed');

      alert('Signup successful!');
      navigate('/chat');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <>
      <Header />
      <main className="min-h-screen bg-black flex items-center justify-center px-4 pt-20">
        <div className="w-full max-w-md bg-gray-900 text-white rounded-2xl shadow-xl p-8">
          <div className="flex flex-col items-center space-y-4">
            <img src={logoText} alt="Logo" className="h-8" />
            <h2 className="text-2xl font-bold">Create your account</h2>
            <p className="text-sm text-gray-400 text-center">Sign up to access AS13.ai</p>
          </div>

          <form onSubmit={handleSubmit} className="mt-6 space-y-4">
            {error && <p className="text-red-500 text-center">{error}</p>}
            <input
              type="email"
              placeholder="Email address"
              value={form.email}
              onChange={(e) => setForm({ ...form, email: e.target.value })}
              className="w-full px-4 py-3 rounded-md bg-gray-800 border border-gray-700 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="password"
              placeholder="Password"
              value={form.password}
              onChange={(e) => setForm({ ...form, password: e.target.value })}
              className="w-full px-4 py-3 rounded-md bg-gray-800 border border-gray-700 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="password"
              placeholder="Confirm Password"
              value={form.confirmPassword}
              onChange={(e) => setForm({ ...form, confirmPassword: e.target.value })}
              className="w-full px-4 py-3 rounded-md bg-gray-800 border border-gray-700 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 rounded-md font-medium"
            >
              Sign up
            </button>
          </form>

          <div className="mt-6 space-y-3">
            <a
              href="http://localhost:5001/auth/google?state=signup"
              className="flex items-center justify-center gap-3 w-full text-center bg-white text-black py-2 rounded-md hover:bg-gray-200"
            >
              <img src={googleIcon} alt="Google" className="h-5" />
              Sign up with Google
            </a>
            <a
              href="http://localhost:5001/auth/microsoft?state=signup"
              className="flex items-center justify-center gap-3 w-full text-center bg-blue-700 text-white py-2 rounded-md hover:bg-blue-800"
            >
              <img src={microsoftIcon} alt="Microsoft" className="h-5" />
              Sign up with Microsoft
            </a>
          </div>

          <p className="mt-6 text-center text-sm text-gray-400">
            Already have an account?{' '}
            <Link to="/login" className="text-blue-500 hover:underline">Log in</Link>
          </p>
        </div>
      </main>
    </>
  );
}
