import { useState, useEffect } from 'react';
import { marked } from 'marked';

export default function Chat() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem("messages");
    return saved ? JSON.parse(saved) : [];
  });
  const [input, setInput] = useState('');

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const sendMessage = async () => {
    const res = await fetch('http://localhost:5000/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: input }),
    });
    const data = await res.json();
    setMessages([
      ...messages,
      { role: 'user', text: input },
      { role: 'ai', text: data.reply }
    ]);
    setInput('');
  };

  return (
    <div className="flex-1 p-10 text-white overflow-y-auto">
      <div className="text-center mb-6">
        <h1 className="text-4xl font-bold">Where ideas begin</h1>
        <p className="text-gray-400 mt-2">Bring ideas to life in seconds...</p>
      </div>

      <div className="max-w-xl mx-auto">
        <textarea
          rows={3}
          placeholder="How can AS13 help you today?"
          className="w-full p-3 bg-zinc-800 text-white rounded border border-blue-500"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button
          onClick={sendMessage}
          className="w-full mt-3 bg-blue-600 hover:bg-blue-700 text-white p-2 rounded"
        >
          Send
        </button>

        <div className="mt-6 space-y-4">
          {messages.map((msg, i) => (
            <div
              key={i}
              className={`p-4 rounded-md whitespace-pre-wrap ${
                msg.role === 'user' ? 'bg-blue-100 text-black' : 'bg-gray-700 text-white'
              }`}
              dangerouslySetInnerHTML={{ __html: marked.parse(msg.text) }}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
