import { useLocation, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import logo from '../icons/logo.svg';
import Sidebar from './Sidebar';

export default function Header() {
  const location = useLocation();
  const navigate = useNavigate();
  const isChatPage = location.pathname === '/chat';
  const isAuthPage = location.pathname === '/login' || location.pathname === '/signup';

  const [dark, setDark] = useState(() => {
    const stored = localStorage.getItem('theme');
    return stored ? stored === 'dark' : true;
  });

  useEffect(() => {
    const forceDark = isAuthPage || dark;
    if (forceDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    if (!isAuthPage) {
      localStorage.setItem('theme', dark ? 'dark' : 'light');
    }
  }, [dark, isAuthPage]);

  const handleLogoClick = () => {
    localStorage.removeItem('messages');
    sessionStorage.removeItem('visited');
    navigate('/chat');
  };

  return (
    <header className="fixed top-0 left-0 w-full md:ml-64 bg-white dark:bg-zinc-900 text-black dark:text-white p-3 md:p-4 shadow-sm z-40 transition-all duration-300 flex items-center justify-between">
      {/* Sidebar Toggle on Chat Page (mobile only) */}
      {isChatPage && <Sidebar dark={dark} setDark={setDark} />}

      {/* Logo */}
      <button
        onClick={handleLogoClick}
        className="flex items-center gap-2 hover:opacity-80 transition-opacity ml-12 md:ml-0"
      >
        <img src={logo} alt="AS13.ai Logo" className="h-7 w-auto" />
        <span className="text-lg font-semibold text-blue-500">AS13.ai</span>
      </button>

      {/* Optional: Dark mode switch or user menu could go here */}
    </header>
  );
}
